void audiocodec_i2s_start_transmit_data(uint32_t *i2s_data_transmit, uint16_t *i2s_data_size)
{
	*i2s_data_size = (i2s_data_struct.buffer_size); 
	uint16_t  main_counter;		//keeps track of the data been transfered to the buffer of the start driver function
	for (main_counter = 0; main_counter < ((i2s_data_struct.buffer_size)); main_counter++)	//move data to buffer for start function, half of the buffer willbe filled
	{
		((uint16_t *)i2s_data_transmit)[(2*main_counter)] = (*(data_address_pointer[i2s_data_struct.data_sample_cou] + i2s_data_struct.data_sample_index_cou));	//move to first 16bit
		i2s_data_struct.data_sample_index_cou ++;
		((uint16_t *)i2s_data_transmit)[(2*main_counter)+1] =  (*(data_address_pointer[i2s_data_struct.data_sample_cou] + i2s_data_struct.data_sample_index_cou));		//move to 2nd 16bit
		i2s_data_struct.data_sample_index_cou ++;
		//if all first sample size has been moved to buffer, move to the next sample size
		//?? am not sure what the -1 is doing there
		if ((i2s_data_struct.data_size[i2s_data_struct.data_sample_cou] - 1) < (i2s_data_struct.data_sample_index_cou))	//  if all data of a sample has been moved out inot the buffer
		{
			i2s_data_struct.data_sample_cou++;	//next data sample
			i2s_data_struct.data_sample_index_cou = 0;	//start from index 0 of next data sample
		}	
	}
}

void audiocodec_volume_control(uint8_t control_option)
{
	switch (control_option)
	{
		volume_cont_reg = &volume_control_data[0];
		case(1):
		if (device_volume < (MAX_AMP_GAIN +1))
			{
				device_volume++;
				volume_control_data[0] = 	HP_R_GAIN_ADD;
				volume_control_data[1] = 	device_volume;
				volume_control_data[2] = 	HP_L_GAIN_ADD	;
				volume_control_data[3] = 	device_volume; 	
				I2C1master_StartTX (Audiocodec_vol , false);	//volume increase
			}
		break;	
		case(0):
			if (device_volume > MIN_AMP_GAIN)
			{
				device_volume--;
				volume_control_data[0] = 	HP_R_GAIN_ADD;
				volume_control_data[1] = 	device_volume;
				volume_control_data[2] = 	HP_L_GAIN_ADD	;
				volume_control_data[3] = 	device_volume;		
				I2C1master_StartTX (Audiocodec_vol, false);	//volume decrease				
			}
		break;
	}
}